mapper.update:
UPDATE forum
forum_name=#{forum_name}, forum_intruction=#{forum_intruction}, user_id=#{user_id}, forum_status=#{forum_status}, prop1=#{prop1}, prop2=#{prop2}, prop3=#{prop3}, prop4=#{prop4}, prop5=#{prop5}, prop6=#{prop6}, prop7=#{prop7}, prop8=#{prop8}, prop9=#{prop9}, prop10=#{prop10}
WHERE forum_id = #{forum_id}

mapper.select:
SELECT

FROM forum

	
		AND user_id = #{user_id}
	
	
		AND forum_parent_id = #{forum_parent_id}
	
	
		AND forum_id = #{forum_id}
	
	
		AND forum_status = #{forum_status}
